import React, {useEffect, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {getFavourites} from "../reducks/favourites/selectors"
import { fetchFromLocalStorage } from '../reducks/favourites/operations'
import Header from '../components/common/Header'
import Movie from '../components/common/Movie'
import Footer from '../components/common/Footer'


const Favourites = () => {
  
 
  const dispatch =useDispatch();
  let favourite = useState("");
  const selector = useSelector( state => state)
  
   favourite = getFavourites(selector)
  
  useEffect(()=>{ 
    dispatch(fetchFromLocalStorage(favourite))
   
  },[]);

  return (
     <>
    <Header/>

        <div class="feature">
      <div className="heading">
        <h2>Favorietes</h2></div>
       
        {favourite.results > 0 && true? (<section class="coming-soon">
          <h1> fafsadklfjlaksjdflkasjfdl</h1>
           {favourite && favourite.map(favourite =>(<Movie movie={favourite}/>))}
            </section> 
          ):(
            <p className='no_favourite'>No Favourite movies here yet...</p>
          )}      
         
          {/* {favourite && favourite.map(favourite =>(<Movie movie={favourite}/>))} */}

           
      </div>
       
    <Footer/>
    
    </>
  )
}

export default Favourites
