import React, { useEffect, useState } from 'react'
import disLiked from "../../assets/img/favorite.svg"
import liked from "../../assets/img/like.svg"
import Trailer_btn from "../../assets/img/Trailer-button.svg"
import star from "../../assets/img/star-rate.svg"
import { useDispatch, useSelector } from 'react-redux'
import { add_Favourite } from '../../reducks/favourites/operations'
import {delete_Favourite} from '../../reducks/favourites/operations'
import { getFavourites } from '../../reducks/favourites/selectors';
 

const Movie = ({movie}) => {
 const dispatch = useDispatch();
 
  const selector = useSelector(state=>state);
  const favourite = getFavourites(selector);
  const [showLike,setShowLike] = useState(true);

  const favouriteMovie=(movie)=>{
     dispatch(add_Favourite(movie));
    console.log(" favourite is added ", movie);
  }
//   const deleteFavouriteMovie=(movie)=>{
//     dispatch(delete_Favourite(movie));
//     console.log(" favourite deleted ");
//   }

  useEffect(()=>{
    let favouriteMovie = favourite.filter(favourite => favourite.id == movie.id);
    if (favouriteMovie.length > 0) {
      setShowLike(false)
    }
  },[favourite])


  console.log("i have the movie");
  return (
           <div class="movie">
          <div class="image">
            <img src={movie.image} alt="movie" />
          </div>
          <div class="like">            
           
           <img src={disLiked} alt =" " onClick={()=>{favouriteMovie(movie)}}/>

           {/* {false?  (<img src={liked} alt =" " onClick={()=>{deleteFavouriteMovie(props.movie)}}/>
           ):(<img src={disLiked} alt =" " onClick={()=>{console.log("the movie is p",props.movie)}}/>)} */}
            
          </div>
          <div class="name">
            <p>{movie.name}</p>
          </div>
          <div class="info">
            <div class="rating">
              <img src={star} alt="star-rate" />
              <p>{movie.rate} <span>{movie.total_rate}</span></p>
            </div>
            <div class="trailer-btn">
              <img src={Trailer_btn} alt="trailer-btn"/>
            </div>
          </div>
        </div>
  )
}

export default Movie
