import React, {useEffect, useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {getFavourites} from "../reducks/favourites/selectors"
import { fetchFromLocalStorage } from '../reducks/favourites/operations'
import Header from '../components/common/Header'
import Movie from '../components/common/Movie'
import Footer from '../components/common/Footer'
import liked from "../assets/img/like.svg"
import favCard from "../components/common/FavCard"
import FavCard from '../components/common/FavCard'

const Favourites = () => {
  
 
  const dispatch =useDispatch();
  let favourite = useState("");
  const selector = useSelector( state => state)
  
   favourite = getFavourites(selector)
  
  useEffect(()=>{ 
    dispatch(fetchFromLocalStorage())
   
  },[]);

  return (
     <>
    <Header/>
   

        <div class="feature">
      <div className="heading">
        <h2>Favorietes</h2></div>
       <div calss="favourite">
        {/* {favourite.length >0 && true? (<section class="coming-soon">
          
           { favourite.map(favourite =>(<FavCard movie={favourite}/>))}
            </section> 
          ):(
            <p className='no_favourite'>No Favourite movies here yet...</p>
          )}      
          */}
          {favourite && favourite.map(favourite =>(<FavCard movie={favourite}/>))}

           
      </div></div>
       
    <Footer/>
    
    </>
  )
}

export default Favourites
