import * as Actions from "./actions"
import initialState from "../store/initialState"
export const FavouritesReducer = (state = initialState.favourites, action)=>{
    switch(action.type){
        case Actions.ADD_FAVOURITES:
            return{
                ...state,
                list : action.list

            }
        case action.DELETE_FAVOURITES:
            return{
                ...state,
                list : action.list

            }    
        case action.FETCH_FAVOURITES:
            return{
                ...state,
                list : action.list

            }
            default:
                return state;    
    }
};
